mapper.select:
select 

from yd_goods_pic
where pic_id = #{picId,jdbcType=INTEGER}
  and type = #{type,jdbcType=BIT}

mapper.delete:
delete from yd_goods_pic
where pic_id = #{picId,jdbcType=INTEGER}
  and type = #{type,jdbcType=BIT}

mapper.insert:
insert into yd_goods_pic (pic_id, type, goods_id, 
  store_id, pic_sort, pic_path, 
  is_default, created_at, updated_at
  )
values (#{picId,jdbcType=INTEGER}, #{type,jdbcType=BIT}, #{goodsId,jdbcType=INTEGER}, 
  #{storeId,jdbcType=INTEGER}, #{picSort,jdbcType=INTEGER}, #{picPath,jdbcType=VARCHAR}, 
  #{isDefault,jdbcType=TINYINT}, #{createdAt,jdbcType=TIMESTAMP}, #{updatedAt,jdbcType=TIMESTAMP}
  )

mapper.update:
update yd_goods_pic
set goods_id = #{goodsId,jdbcType=INTEGER},
  store_id = #{storeId,jdbcType=INTEGER},
  pic_sort = #{picSort,jdbcType=INTEGER},
  pic_path = #{picPath,jdbcType=VARCHAR},
  is_default = #{isDefault,jdbcType=TINYINT},
  created_at = #{createdAt,jdbcType=TIMESTAMP},
  updated_at = #{updatedAt,jdbcType=TIMESTAMP}
where pic_id = #{picId,jdbcType=INTEGER}
  and type = #{type,jdbcType=BIT}
